mapper.select:
SELECT p.product_id AS productId, p.picture_base64 AS pictureBase64, p.name, p.description, p.price, p.features, p.average_rating AS averageRating, p.created_at AS createdAt, i.quantity AS quantity FROM product p left JOIN inventory i ON p.product_id = i.product_id WHERE 1=1
AND p.name LIKE CONCAT('%', #{name}, '%')
AND p.average_rating >= #{rating}
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM product
WHERE 1=1

   AND name LIKE CONCAT('%', #{name}, '%')


    AND average_rating >= #{rating}


    AND price  >= #{minPrice}


    AND price <= #{maxPrice}
